mapper.sql:
select pmb.* ,project.project_name,project.project_no ,sc.staff_no,sc.staff_name, aa.need_approve, pmo.join_datetime,pmo.confirmation_date,pmo.tryout_salary,pmo.official_salary,pmo.curr_salary from personnel_monthly_base pmb inner join personnel_monthly_official pmo on pmb.id = pmo.id inner join tb_staff_costs sc on pmb.staff_id = sc.staff_id left JOIN tb_project project on pmb.project_id = project.project_id left join (SELECT need_approve,data_id FROM tb_apply_approve where delete_flag='0' and need_approve='1' and data_type = 'PERSONNELMONTHLYBASE' group by data_id) aa on pmb.id = aa.data_id WHERE 1=1
AND pmb.the_month = #{PersonnelMonthlyOfficial.the_month}
AND pmb.staff_id = #{PersonnelMonthlyOfficial.staff_id}
AND pmb.monthly_type = #{PersonnelMonthlyOfficial.monthly_type}
and pmb.verify_userid is not null
and pmb.verify_userid is null
order by pmb.monthly_type, pmb.the_month,pmb.build_datetime , pmb.import_order
